   private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
       // TODO add your handling code here:
	   //SAVE BUTTON code
	   ContactDetails cd= new ContactDetails(jTextField1.getText(), jTextField2.getText(), jTextField3.getText(), jTextField4.getText()
			   ,jTextField5.getText(), jTextField6.getText(), jTextField7.getText(), jTextField8.getText());
	   ContactsListGUI.phoneTrie.Insert(jTextField3.getText(), cd);
	   ContactsListGUI.fNameTrie.Insert(jTextField1.getText(), cd);
	   ContactsListGUI.lNameTrie.Insert(jTextField2.getText(), cd);
	   
	   //Displaying the new contact on the JList immediately
	   DisplayNumber();
	   //Serializing the new phoneTrie
	   try {
		   ObjectOutputStream out= new ObjectOutputStream(new FileOutputStream("trie3.ser"));
		   out.writeObject(ContactsListGUI.phoneTrie);
		   out.close();
	   }
	   catch (IOException e) {
		   e.printStackTrace();
	   }
	   //Serializing the new fNameTrie
	   try {
		   ObjectOutputStream out2= new ObjectOutputStream(new FileOutputStream("trie.ser"));
		   out2.writeObject(ContactsListGUI.fNameTrie);
		   out2.close();
	   }
	   catch (IOException e) {
		   e.printStackTrace();
	   }
	   //Serializing the new lNameTrie
	   try {
		   ObjectOutputStream out3= new ObjectOutputStream(new FileOutputStream("trie2.ser"));
		   out3.writeObject(ContactsListGUI.lNameTrie);
		   out3.close();
	   }
	   catch (IOException e) {
		   e.printStackTrace();
	   }
   } 

