	public void DisplayNumber(){
	    ToDisplayNumber(ContactsListGUI.phoneTrie.rootNumber);
	}
	public NodeNumber ToDisplayNumber(NodeNumber rootN){

//	    for(int i = 0; i < 1; i++){
	        
	        if(rootN.zero != null){
	        ToDisplayNumber(rootN.zero);
	        }
	        
	        if(rootN.i != null){
	        ToDisplayNumber(rootN.i);
	        }
	        
	        if(rootN.ii != null){
	        ToDisplayNumber(rootN.ii);
	        }
	        
	        if(rootN.iii != null){
	        ToDisplayNumber(rootN.iii);
	        }
	        
	        if(rootN.iv != null){
	        ToDisplayNumber(rootN.iv);
	        }
	        
	        if(rootN.v != null){
	        ToDisplayNumber(rootN.v);
	        }
	        
	        if(rootN.vi != null){
	        ToDisplayNumber(rootN.vi);
	        }
	        
	        if(rootN.vii != null){
	        ToDisplayNumber(rootN.vii);
	        } 
	        
	        if(rootN.viii != null){
	        ToDisplayNumber(rootN.viii);
	        } 
	        
	        if(rootN.ix != null){
	        ToDisplayNumber(rootN.ix);
	        }        
	        
	        if(rootN.end == true && rootN.ContactD.PNumber.equals(jTextField3.getText())){
	        	ContactsListGUI.listNames.addElement(rootN.ContactD.FName+" "+rootN.ContactD.LName+": "+rootN.ContactD.PNumber);
	        	//rootN.ContactD.printNumber();
	        }
	        else{
	        }
	  //  }
	return rootN;
	}

